mapper.select:
select
id, userid, total_cashout, total_send_bzz, total_get_bzz, bzz, total_node_num
from nprox.swarm_user_total


        and id = #{id}


        and userid = #{userid}


        and total_cashout = #{totalCashout}


        and total_send_bzz = #{totalSendBzz}


        and total_get_bzz = #{totalGetBzz}


        and bzz = #{bzz}


        and total_node_num = #{totalNodeNum}
